create database yojo;

use yojo;

create table position
(
    position_name varchar(100) not null primary key,
    position_type int          not null,
    image_path    varchar(200) not null,
    description   text         not null,
    author        varchar(50)  not null
);

create table result
(
    image_name    varchar(50)  not null primary key,
    position_name varchar(100) not null,
    score         text         not null,
    author        varchar(50)  not null,
    created_at    datetime     not null,
    image_path    varchar(200) not null,
    foreign key (position_name) references position (position_name) on update cascade
);

create table keypoint
(
    image_name varchar(50) not null,
    width      int         not null,
    height     int         not null,
    keypoints  text        not null,
    foreign key (image_name) references result (image_name) on update cascade
);


